mapper.select:
SELECT PAGE_BOARD.* FROM ( SELECT ROW_NUMBER() OVER (ORDER BY NUM desc) rn, CASE CODE_TYPE WHEN '01' THEN '자유' WHEN '02' THEN '익명' WHEN '03' THEN 'QnA' END AS CODETYPE, NUM, NAME, TITLE, CONTENT, TO_CHAR(REGDATE , 'YYYY/MM/DD') AS REGDATE FROM FREEBOARD
WHERE NUM = #{keyword}
WHERE TITLE like '%'||#{keyword}||'%'
) PAGE_BOARD WHERE rn BETWEEN #{start} and #{end}

SELECT COUNT(*) CNT FROM FREEBOARD

	WHERE NUM = #{keyword}


	WHERE TITLE like '%'||#{keyword}||'%'

mapper.update:
UPDATE FREEBOARD
SET CODE_TYPE = #{dto.codeType}, TITLE = #{dto.title}, CONTENT = #{dto.content}
WHERE NUM = #{dto.num}
